import React, { useState } from 'react';
import axios from 'axios';

const AddTargetForm = ({ onClose }) => {
  const [targetType, setTargetType] = useState('domain');
  const [targetValue, setTargetValue] = useState('');
  const [targetDescription, setTargetDescription] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/targets', {
        type: targetType,
        value: targetValue,
        description: targetDescription,
      });
      console.log(response.data);
      onClose();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="target-type">
          Target Type
        </label>
        <select
          id="target-type"
          value={targetType}
          onChange={(e) => setTargetType(e.target.value)}
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
        >
          <option value="domain">Domain</option>
          <option value="ip">IP Address</option>
          <option value="url">URL</option>
        </select>
      </div>
      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="target-value">
          Target Value
        </label>
        <input
          id="target-value"
          type="text"
          placeholder="e.g., example.com"
          value={targetValue}
          onChange={(e) => setTargetValue(e.target.value)}
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
        />
      </div>
      <div className="mb-6">
        <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="target-description">
          Description
        </label>
        <textarea
          id="target-description"
          placeholder="A short description of the target"
          value={targetDescription}
          onChange={(e) => setTargetDescription(e.target.value)}
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
        ></textarea>
      </div>
      <div className="flex items-center justify-between">
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        >
          Add Target
        </button>
        <button
          type="button"
          onClick={onClose}
          className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        >
          Cancel
        </button>
      </div>
    </form>
  );
};

export default AddTargetForm;
