import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = ({ isCollapsed, toggleSidebar, isMobileNavOpen, toggleMobileNav }) => {
  const [modesOpen, setModesOpen] = useState(false);
  const [payloadsOpen, setPayloadsOpen] = useState(false);
  const location = useLocation();

  const isActive = (path) => location.pathname === path;

  const sidebarClasses = `
    fixed inset-y-0 left-0 z-20
    flex flex-col 
    w-60 bg-surface-light shadow-lg 
    transform transition-all duration-300 ease-in-out
    ${isMobileNavOpen ? 'translate-x-0' : '-translate-x-full'}
    md:relative md:translate-x-0 
    ${isCollapsed ? 'md:w-16' : 'md:w-60'}
  `;

  const handleLinkClick = () => {
    if (isMobileNavOpen) {
      toggleMobileNav();
    }
  };

  return (
    <aside id="sidebar" className={sidebarClasses}>
      <div className="p-4 flex items-center justify-between h-18 border-b border-gray-700">
        <div className="flex items-center">
          <img id="sidebar-logo" src="/images/Logo.png" alt="CyberLynx Logo" className="h-10 w-10 object-contain rounded-lg" />
          <h1 id="sidebar-title" className={`text-2xl font-bold text-primary ml-3 ${isCollapsed ? 'md:hidden' : ''}`}>CyberLynX</h1>
        </div>
        <button onClick={toggleMobileNav} className="md:hidden text-white">
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path></svg>
        </button>
      </div>

      <nav className="mt-6 flex-grow">
        <ul className="space-y-2">
          <li>
            <Link to="/" onClick={handleLinkClick} className={`flex items-center py-2 px-4 ${isActive('/') ? 'text-white bg-primary rounded' : 'text-gray-300 hover:bg-gray-700 hover:text-white'} transition-colors duration-200`} data-tooltip="Dashboard">
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6"></path></svg>
              <span className={`ml-3 ${isCollapsed ? 'md:hidden' : ''}`}>Dashboard</span>
            </Link>
          </li>
          {/* ... other links ... */}
        </ul>
      </nav>

      <div className="p-4 border-t border-gray-700">
        <button onClick={toggleSidebar} id="sidebar-toggle" className="w-full mt-2 py-2 px-4 bg-gray-700 hover:bg-gray-600 rounded-md text-white hidden md:flex items-center justify-center transition-colors duration-200">
          <svg id="sidebar-icon" className={`w-5 h-5 transform ${isCollapsed ? 'rotate-180' : ''}`} fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M11 19l-7-7 7-7m8 14l-7-7 7-7"></path></svg>
          <span className={`ml-2 ${isCollapsed ? 'hidden' : ''}`}>{isCollapsed ? 'Expand' : 'Collapse'}</span>
        </button>
      </div>
    </aside>
  );
};

export default Sidebar;
