import React, { useState } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import MainContent from './components/MainContent';
import Modal from './components/Modal';
import AddTargetForm from './components/AddTargetForm';
import './App.css';

function App() {
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [showAddTargetModal, setShowAddTargetModal] = useState(false);

  const toggleSidebar = () => {
    setIsCollapsed(!isCollapsed);
  };

  const handleAddTargetClick = () => {
    setShowAddTargetModal(true);
  };

  const handleCloseAddTargetModal = () => {
    setShowAddTargetModal(false);
  };

  return (
    <Router>
      <div id="app" className="flex min-h-screen w-full">
        <Sidebar isCollapsed={isCollapsed} toggleSidebar={toggleSidebar} onAddTargetClick={handleAddTargetClick} />
        <div className={`flex-grow transition-all duration-300 ease-in-out ${isCollapsed ? 'ml-16' : 'ml-60'}`}>
          <MainContent />
        </div>
      </div>
      <Modal show={showAddTargetModal} onClose={handleCloseAddTargetModal}>
        <AddTargetForm onClose={handleCloseAddTargetModal} />
      </Modal>
    </Router>
  );
}

export default App;
