import React, { useState, useRef, useEffect } from 'react';
import WorkflowCanvas from '../components/workflow/WorkflowCanvas';
import WorkflowTerminal from '../components/workflow/WorkflowTerminal';

const Workflow = () => {
  const [nodes, setNodes] = useState([]);
  const [connections, setConnections] = useState([]);
  const [isTerminalMinimized, setIsTerminalMinimized] = useState(false);
  const [terminalHeight, setTerminalHeight] = useState(300);
  const [sidebarWidth, setSidebarWidth] = useState(240);
  const containerRef = useRef(null);

  useEffect(() => {
    // Detect sidebar width from DOM
    const sidebar = document.getElementById('sidebar');
    if (sidebar) {
      const updateSidebarWidth = () => {
        const width = sidebar.offsetWidth;
        setSidebarWidth(width);
      };
      updateSidebarWidth();

      const observer = new MutationObserver(updateSidebarWidth);
      observer.observe(sidebar, { 
        attributes: true, 
        attributeFilter: ['class'],
        childList: false,
        subtree: false
      });

      window.addEventListener('resize', updateSidebarWidth);

      return () => {
        observer.disconnect();
        window.removeEventListener('resize', updateSidebarWidth);
      };
    }
  }, []);


  useEffect(() => {
    const handleMouseMove = (e) => {
      if (isResizing) {
        const newHeight = window.innerHeight - e.clientY;
        setTerminalHeight(Math.max(200, Math.min(600, newHeight)));
      }
    };

    const handleMouseUp = () => {
      setIsResizing(false);
    };

    if (isResizing) {
      window.addEventListener('mousemove', handleMouseMove);
      window.addEventListener('mouseup', handleMouseUp);
      return () => {
        window.removeEventListener('mousemove', handleMouseMove);
        window.removeEventListener('mouseup', handleMouseUp);
      };
    }
  }, [isResizing]);

  return (
    <div className="h-full flex flex-col">
      <div className="mb-4">
        <h2 className="text-2xl font-semibold text-text-primary mb-1">Workflow Builder</h2>
        <p className="text-sm text-text-secondary">Design and customize your security assessment workflows</p>
      </div>
      <div ref={containerRef} className="flex-1 flex flex-col gap-0 overflow-hidden relative">
        <div className="bg-surface-light rounded-lg overflow-hidden transition-all duration-200">
          <WorkflowCanvas 
            onNodesChange={setNodes}
            onConnectionsChange={setConnections}
          />
        </div>
      </div>
      {/* Floating Terminal */}
      <TerminalButton 
        onClick={() => setIsTerminalMinimized(false)}
        isMinimized={isTerminalMinimized}
      />
      <WorkflowTerminal 
        nodes={nodes} 
        connections={connections}
        isMinimized={isTerminalMinimized}
        onMinimize={() => setIsTerminalMinimized(true)}
        onMaximize={() => setIsTerminalMinimized(false)}
      />
    </div>
  );
};

export default Workflow;
