import React, { useState, useRef, useEffect } from 'react';
import ToolNode from './ToolNode';
import ToolConfigModal from './ToolConfigModal';

const WorkflowCanvas = () => {
  const [nodes, setNodes] = useState([]);
  const [connections, setConnections] = useState([]);
  const [selectedNode, setSelectedNode] = useState(null);
  const [configModalOpen, setConfigModalOpen] = useState(false);
  const [configTool, setConfigTool] = useState(null);
  const [dragging, setDragging] = useState(null);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const canvasRef = useRef(null);
  const nodeIdCounter = useRef(0);

  const availableTools = [
    { name: 'Nmap', icon: 'nmap' },
    { name: 'Subfinder', icon: 'subfinder' },
    { name: 'Dirsearch', icon: 'dirsearch' },
    { name: 'Ghauri', icon: 'ghauri' }
  ];

  const addNode = (toolName) => {
    const newId = `node-${nodeIdCounter.current++}`;
    const newNode = {
      id: newId,
      name: toolName,
      position: {
        x: Math.random() * 400 + 100,
        y: Math.random() * 300 + 100
      },
      config: {}
    };
    setNodes([...nodes, newNode]);
  };

  const handleNodeClick = (node) => {
    setSelectedNode(node.id);
    setConfigTool(node);
    setConfigModalOpen(true);
  };

  const handleSaveConfig = (nodeId, config) => {
    setNodes(prevNodes =>
      prevNodes.map(node =>
        node.id === nodeId ? { ...node, config } : node
      )
    );
  };

  const handleDeleteNode = (nodeId) => {
    setNodes(prevNodes => prevNodes.filter(node => node.id !== nodeId));
    setConnections(prevConnections =>
      prevConnections.filter(conn => conn.from !== nodeId && conn.to !== nodeId)
    );
    if (selectedNode === nodeId) {
      setSelectedNode(null);
    }
  };

  const handleMouseDown = (nodeId, e) => {
    e.stopPropagation();
    const node = nodes.find(n => n.id === nodeId);
    if (node) {
      setDragging(nodeId);
      setDragOffset({
        x: e.clientX - node.position.x,
        y: e.clientY - node.position.y
      });
    }
  };

  const handleMouseMove = (e) => {
    if (dragging) {
      setNodes(prevNodes =>
        prevNodes.map(node =>
          node.id === dragging
            ? {
                ...node,
                position: {
                  x: e.clientX - dragOffset.x,
                  y: e.clientY - dragOffset.y
                }
              }
            : node
        )
      );
    }
  };

  const handleMouseUp = () => {
    setDragging(null);
  };

  useEffect(() => {
    if (dragging) {
      window.addEventListener('mousemove', handleMouseMove);
      window.addEventListener('mouseup', handleMouseUp);
      return () => {
        window.removeEventListener('mousemove', handleMouseMove);
        window.removeEventListener('mouseup', handleMouseUp);
      };
    }
  }, [dragging, dragOffset]);

  const drawConnection = (fromNode, toNode) => {
    const fromX = fromNode.position.x + 70; // Center of node
    const fromY = fromNode.position.y + 80; // Bottom of node
    const toX = toNode.position.x + 70;
    const toY = toNode.position.y; // Top of node

    const midY = (fromY + toY) / 2;

    return (
      <path
        key={`${fromNode.id}-${toNode.id}`}
        d={`M ${fromX} ${fromY} C ${fromX} ${midY}, ${toX} ${midY}, ${toX} ${toY}`}
        stroke="rgba(255, 109, 31, 0.6)"
        strokeWidth="2"
        fill="none"
        markerEnd="url(#arrowhead)"
        className="cursor-pointer hover:stroke-primary transition-colors"
      />
    );
  };

  return (
    <div className="relative w-full h-full">
      {/* Toolbar */}
      <div className="absolute top-4 left-4 z-20 bg-surface-light rounded-xl shadow-lg p-4">
        <h3 className="text-sm font-semibold text-text-primary mb-3">Add Tools</h3>
        <div className="flex flex-col space-y-2">
          {availableTools.map((tool) => (
            <button
              key={tool.name}
              onClick={() => addNode(tool.name)}
              className="px-4 py-2 bg-gradient-to-r from-primary to-primary-dark hover:from-primary-dark hover:to-primary text-white text-sm font-semibold rounded-lg shadow-md hover:shadow-lg transform hover:-translate-y-0.5 transition-all duration-200 flex items-center space-x-2"
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 6v6m0 0v6m0-6h6m-6 0H6"></path>
              </svg>
              <span>{tool.name}</span>
            </button>
          ))}
        </div>
        {nodes.length > 0 && (
          <button
            onClick={() => {
              setNodes([]);
              setConnections([]);
              setSelectedNode(null);
            }}
            className="mt-3 w-full px-4 py-2 bg-danger hover:bg-red-600 text-white text-sm font-semibold rounded-lg transition-all duration-200"
          >
            Clear All
          </button>
        )}
      </div>

      {/* Canvas */}
      <div
        ref={canvasRef}
        className="relative w-full h-full bg-background-primary border-2 border-dashed border-gray-300 dark:border-gray-600 rounded-xl overflow-hidden"
        style={{ minHeight: '600px' }}
        onClick={() => setSelectedNode(null)}
      >
        {/* SVG for connections */}
        <svg className="absolute inset-0 pointer-events-none" style={{ width: '100%', height: '100%' }}>
          <defs>
            <marker
              id="arrowhead"
              markerWidth="10"
              markerHeight="10"
              refX="9"
              refY="3"
              orient="auto"
            >
              <polygon
                points="0 0, 10 3, 0 6"
                fill="rgba(255, 109, 31, 0.6)"
              />
            </marker>
          </defs>
          {connections.map(conn => {
            const fromNode = nodes.find(n => n.id === conn.from);
            const toNode = nodes.find(n => n.id === conn.to);
            if (fromNode && toNode) {
              return drawConnection(fromNode, toNode);
            }
            return null;
          })}
        </svg>

        {/* Nodes */}
        {nodes.map((node) => (
          <div key={node.id} className="relative">
            <ToolNode
              tool={node}
              position={node.position}
              onClick={() => handleNodeClick(node)}
              isSelected={selectedNode === node.id}
            />
            {/* Delete button */}
            {selectedNode === node.id && (
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  handleDeleteNode(node.id);
                }}
                className="absolute -top-2 -right-2 w-6 h-6 bg-danger hover:bg-red-600 text-white rounded-full flex items-center justify-center shadow-lg z-20 transition-all duration-200"
                style={{
                  left: `${node.position.x + 140}px`,
                  top: `${node.position.y}px`
                }}
              >
                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
                </svg>
              </button>
            )}
            {/* Drag handle */}
            <div
              className="absolute cursor-move opacity-0 hover:opacity-100 transition-opacity"
              style={{
                left: `${node.position.x}px`,
                top: `${node.position.y}px`,
                width: '140px',
                height: '80px'
              }}
              onMouseDown={(e) => handleMouseDown(node.id, e)}
            ></div>
          </div>
        ))}

        {/* Empty state */}
        {nodes.length === 0 && (
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="text-center">
              <svg className="w-24 h-24 mx-auto text-text-secondary opacity-50 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z"></path>
              </svg>
              <p className="text-text-secondary text-lg font-semibold">No tools added yet</p>
              <p className="text-text-secondary text-sm mt-2">Click on a tool in the sidebar to add it to your workflow</p>
            </div>
          </div>
        )}
      </div>

      {/* Configuration Modal */}
      {configTool && (
        <ToolConfigModal
          tool={configTool}
          isOpen={configModalOpen}
          onClose={() => {
            setConfigModalOpen(false);
            setConfigTool(null);
          }}
          onSave={handleSaveConfig}
        />
      )}

      {/* Connection Helper */}
      {nodes.length > 1 && (
        <div className="absolute bottom-4 right-4 z-20 bg-surface-light rounded-xl shadow-lg p-4">
          <p className="text-xs text-text-secondary mb-2">ðŸ’¡ Tip: Drag nodes to reposition them</p>
          <p className="text-xs text-text-secondary">Click on a node to configure it</p>
        </div>
      )}
    </div>
  );
};

export default WorkflowCanvas;
