import React, { useState, useRef, useEffect } from 'react';

const WorkflowTerminal = ({ nodes, connections }) => {
  const [commands, setCommands] = useState([]);
  const [output, setOutput] = useState('');
  const [isExecuting, setIsExecuting] = useState(false);
  const [selectedCommandIndex, setSelectedCommandIndex] = useState(null);
  const terminalRef = useRef(null);
  const outputRef = useRef(null);

  useEffect(() => {
    generateCommands();
  }, [nodes, connections]);

  useEffect(() => {
    if (outputRef.current) {
      outputRef.current.scrollTop = outputRef.current.scrollHeight;
    }
  }, [output]);

  const generateCommands = () => {
    if (nodes.length === 0) {
      setCommands([]);
      return;
    }

    // Build execution order based on connections
    const executionOrder = buildExecutionOrder(nodes, connections);
    const generatedCommands = executionOrder.map((node, index) => {
      return {
        id: node.id,
        tool: node.name,
        command: buildCommand(node),
        order: index + 1
      };
    });

    setCommands(generatedCommands);
  };

  const buildExecutionOrder = (nodes, connections) => {
    // Simple topological sort - nodes without incoming connections first
    const ordered = [];
    const visited = new Set();
    const inDegree = new Map();

    nodes.forEach(node => {
      inDegree.set(node.id, 0);
    });

    connections.forEach(conn => {
      inDegree.set(conn.to, (inDegree.get(conn.to) || 0) + 1);
    });

    const queue = nodes.filter(node => inDegree.get(node.id) === 0);

    while (queue.length > 0) {
      const node = queue.shift();
      if (!visited.has(node.id)) {
        visited.add(node.id);
        ordered.push(node);

        connections
          .filter(conn => conn.from === node.id)
          .forEach(conn => {
            const targetNode = nodes.find(n => n.id === conn.to);
            if (targetNode) {
              const newDegree = (inDegree.get(conn.to) || 0) - 1;
              inDegree.set(conn.to, newDegree);
              if (newDegree === 0) {
                queue.push(targetNode);
              }
            }
          });
      }
    }

    // Add any remaining nodes (disconnected)
    nodes.forEach(node => {
      if (!visited.has(node.id)) {
        ordered.push(node);
      }
    });

    return ordered;
  };

  const buildCommand = (node) => {
    const config = node.config || {};
    const toolName = node.name.toLowerCase();

    switch (toolName) {
      case 'nmap':
        const target = config.target || 'TARGET';
        const scanType = config.scan_type ? config.scan_type.split(' ')[0] : '-sS';
        const ports = config.ports || '1-1000';
        const timing = config.timing ? config.timing.split(' ')[0] : '-T3';
        const scripts = config.scripts ? `--script=${config.scripts}` : '';
        const outputFlag = config.output === 'XML' ? '-oX' : config.output === 'Grepable' ? '-oG' : config.output === 'All' ? '-oA' : '';
        const outputFile = outputFlag ? ` ${outputFlag} output` : '';
        return `nmap ${scanType} ${timing} ${scripts} ${ports} ${target}${outputFile}`.replace(/\s+/g, ' ').trim();

      case 'subfinder':
        const domain = config.domain || 'example.com';
        const sources = config.sources ? `-sources ${config.sources}` : '';
        const recursive = config.recursive ? '-recursive' : '';
        const threads = config.threads ? `-t ${config.threads}` : '';
        const timeout = config.timeout ? `-timeout ${config.timeout}` : '';
        const outputFileSub = config.output ? `-o ${config.output}` : '';
        return `subfinder -d ${domain} ${sources} ${recursive} ${threads} ${timeout} ${outputFileSub}`.replace(/\s+/g, ' ').trim();

      case 'dirsearch':
        const url = config.url || 'https://example.com';
        const wordlist = config.wordlist ? `-w ${config.wordlist}` : '-w /usr/share/wordlists/dirb/common.txt';
        const extensions = config.extensions ? `-e ${config.extensions}` : '';
        const threadsDir = config.threads ? `-t ${config.threads}` : '';
        const recursiveDir = config.recursive ? '--recursive' : '';
        const statusCodes = config.status_codes ? `--status-codes ${config.status_codes}` : '';
        return `python3 dirsearch.py -u ${url} ${wordlist} ${extensions} ${threadsDir} ${recursiveDir} ${statusCodes}`.replace(/\s+/g, ' ').trim();

      case 'ghauri':
        const ghauriUrl = config.url || 'http://example.com/vuln.php?id=1';
        const method = config.method || 'GET';
        const data = config.data ? `--data "${config.data}"` : '';
        const level = config.level ? `--level ${config.level.split(' ')[0]}` : '';
        const risk = config.risk ? `--risk ${config.risk}` : '';
        const technique = config.technique ? `--technique ${config.technique}` : '';
        return `ghauri -u "${ghauriUrl}" ${method === 'POST' ? `-X POST ${data}` : ''} ${level} ${risk} ${technique}`.replace(/\s+/g, ' ').trim();

      default:
        return `# ${node.name} - Configuration needed`;
    }
  };

  const executeCommand = async (command, index) => {
    setSelectedCommandIndex(index);
    setIsExecuting(true);
    setOutput(`$ ${command.command}\n`);
    setOutput(prev => prev + 'Executing...\n');

    // Simulate command execution (in real app, this would call backend API)
    setTimeout(() => {
      setOutput(prev => prev + `[INFO] Command executed: ${command.tool}\n`);
      setOutput(prev => prev + `[INFO] This is a simulation. In production, this would execute the actual command.\n`);
      setOutput(prev => prev + `[INFO] To execute real commands, integrate with your backend API.\n\n`);
      setIsExecuting(false);
    }, 1500);
  };

  const executeAll = async () => {
    if (commands.length === 0) return;

    setIsExecuting(true);
    setOutput('=== Executing Workflow ===\n\n');

    for (let i = 0; i < commands.length; i++) {
      setSelectedCommandIndex(i);
      setOutput(prev => prev + `[${i + 1}/${commands.length}] $ ${commands[i].command}\n`);
      setOutput(prev => prev + 'Executing...\n');

      await new Promise(resolve => setTimeout(resolve, 1500));

      setOutput(prev => prev + `[INFO] ${commands[i].tool} completed\n\n`);
    }

    setOutput(prev => prev + '=== Workflow Execution Complete ===\n');
    setIsExecuting(false);
    setSelectedCommandIndex(null);
  };

  const copyCommand = (command) => {
    navigator.clipboard.writeText(command);
    setOutput(prev => prev + `[INFO] Command copied to clipboard\n`);
  };

  const clearOutput = () => {
    setOutput('');
  };

  return (
    <div className="flex flex-col h-full bg-gray-900 rounded-lg overflow-hidden border border-gray-700">
      {/* Terminal Header */}
      <div className="flex items-center justify-between px-4 py-2 bg-gray-800 border-b border-gray-700">
        <div className="flex items-center space-x-2">
          <div className="flex space-x-1.5">
            <div className="w-3 h-3 rounded-full bg-red-500"></div>
            <div className="w-3 h-3 rounded-full bg-yellow-500"></div>
            <div className="w-3 h-3 rounded-full bg-green-500"></div>
          </div>
          <span className="text-xs text-gray-400 ml-2">Terminal - Workflow Commands</span>
        </div>
        <div className="flex items-center space-x-2">
          <button
            onClick={executeAll}
            disabled={commands.length === 0 || isExecuting}
            className="px-3 py-1 text-xs bg-green-600 hover:bg-green-700 disabled:bg-gray-700 disabled:cursor-not-allowed text-white rounded transition-colors flex items-center space-x-1"
          >
            <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z" />
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <span>Run All</span>
          </button>
          <button
            onClick={clearOutput}
            className="px-3 py-1 text-xs bg-gray-700 hover:bg-gray-600 text-white rounded transition-colors"
          >
            Clear
          </button>
        </div>
      </div>

      <div className="flex flex-1 overflow-hidden">
        {/* Commands Panel */}
        <div className="w-1/2 border-r border-gray-700 flex flex-col">
          <div className="px-3 py-2 bg-gray-800 border-b border-gray-700">
            <span className="text-xs font-semibold text-gray-300">Generated Commands ({commands.length})</span>
          </div>
          <div className="flex-1 overflow-y-auto p-2 space-y-2">
            {commands.length === 0 ? (
              <div className="text-center text-gray-500 text-sm py-8">
                No commands generated. Add tools to your workflow.
              </div>
            ) : (
              commands.map((cmd, index) => (
                <div
                  key={cmd.id}
                  className={`p-3 rounded border transition-all ${
                    selectedCommandIndex === index
                      ? 'bg-gray-800 border-primary'
                      : 'bg-gray-800 border-gray-700 hover:border-gray-600'
                  }`}
                >
                  <div className="flex items-start justify-between mb-2">
                    <div className="flex items-center space-x-2">
                      <span className="text-xs text-gray-400">#{cmd.order}</span>
                      <span className="text-xs font-semibold text-primary">{cmd.tool}</span>
                    </div>
                    <div className="flex space-x-1">
                      <button
                        onClick={() => copyCommand(cmd.command)}
                        className="p-1 text-gray-400 hover:text-white transition-colors"
                        title="Copy command"
                      >
                        <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 16H6a2 2 0 01-2-2V6a2 2 0 012-2h8a2 2 0 012 2v2m-6 12h8a2 2 0 002-2v-8a2 2 0 00-2-2h-8a2 2 0 00-2 2v8a2 2 0 002 2z" />
                        </svg>
                      </button>
                      <button
                        onClick={() => executeCommand(cmd, index)}
                        disabled={isExecuting}
                        className="p-1 text-gray-400 hover:text-green-400 transition-colors disabled:opacity-50"
                        title="Execute command"
                      >
                        <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M14.752 11.168l-3.197-2.132A1 1 0 0010 9.87v4.263a1 1 0 001.555.832l3.197-2.132a1 1 0 000-1.664z" />
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                        </svg>
                      </button>
                    </div>
                  </div>
                  <code className="text-xs text-gray-300 font-mono block break-all bg-gray-900 p-2 rounded">
                    {cmd.command}
                  </code>
                </div>
              ))
            )}
          </div>
        </div>

        {/* Output Panel */}
        <div className="w-1/2 flex flex-col">
          <div className="px-3 py-2 bg-gray-800 border-b border-gray-700">
            <span className="text-xs font-semibold text-gray-300">Output</span>
          </div>
          <div
            ref={outputRef}
            className="flex-1 overflow-y-auto p-4 font-mono text-xs text-green-400 bg-gray-900"
            style={{ fontFamily: 'Monaco, Menlo, "Ubuntu Mono", monospace' }}
          >
            {output || (
              <div className="text-gray-500">
                <div className="mb-2"># Terminal Output</div>
                <div># Commands will appear here when executed</div>
                <div># Click "Run All" or execute individual commands</div>
              </div>
            )}
            {isExecuting && (
              <span className="inline-block w-2 h-4 bg-green-400 animate-pulse ml-1"></span>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default WorkflowTerminal;
