import React, { useState, useRef, useEffect } from 'react';
import WorkflowCanvas from '../components/workflow/WorkflowCanvas';
import WorkflowTerminal from '../components/workflow/WorkflowTerminal';

const Workflow = () => {
  const [nodes, setNodes] = useState([]);
  const [connections, setConnections] = useState([]);
  const [terminalHeight, setTerminalHeight] = useState(300);
  const [isResizing, setIsResizing] = useState(false);
  const containerRef = useRef(null);

  useEffect(() => {
    const handleMouseMove = (e) => {
      if (isResizing && containerRef.current) {
        const rect = containerRef.current.getBoundingClientRect();
        const newHeight = window.innerHeight - e.clientY - rect.top;
        setTerminalHeight(Math.max(200, Math.min(600, newHeight)));
      }
    };

    const handleMouseUp = () => {
      setIsResizing(false);
    };

    if (isResizing) {
      window.addEventListener('mousemove', handleMouseMove);
      window.addEventListener('mouseup', handleMouseUp);
      return () => {
        window.removeEventListener('mousemove', handleMouseMove);
        window.removeEventListener('mouseup', handleMouseUp);
      };
    }
  }, [isResizing]);

  return (
    <div className="h-full flex flex-col">
      <div className="mb-4">
        <h2 className="text-2xl font-semibold text-text-primary mb-1">Workflow Builder</h2>
        <p className="text-sm text-text-secondary">Design and customize your security assessment workflows</p>
      </div>
      <div ref={containerRef} className="flex-1 flex flex-col gap-0 overflow-hidden relative">
        <div 
          className="bg-surface-light rounded-lg overflow-hidden transition-all duration-200"
          style={{ 
            marginBottom: '0',
            paddingBottom: `${terminalHeight}px`
          }}
        >
          <WorkflowCanvas 
            onNodesChange={setNodes}
            onConnectionsChange={setConnections}
          />
        </div>
        <div 
          className="absolute bottom-0 left-0 right-0 bg-transparent flex-shrink-0 z-10 shadow-2xl"
          style={{ height: `${terminalHeight}px`, maxHeight: '60%' }}
        >
          <div 
            className="w-full h-1 bg-gray-800 hover:bg-gray-700 cursor-ns-resize flex items-center justify-center group"
            onMouseDown={(e) => {
              e.preventDefault();
              setIsResizing(true);
            }}
          >
            <div className="w-12 h-0.5 bg-gray-600 group-hover:bg-gray-500 rounded transition-colors"></div>
          </div>
          <WorkflowTerminal nodes={nodes} connections={connections} />
        </div>
      </div>
    </div>
  );
};

export default Workflow;
