import React, { useState, useRef, useEffect } from 'react';
import ToolNode from './ToolNode';
import ToolConfigModal from './ToolConfigModal';

const WorkflowCanvas = () => {
  const [nodes, setNodes] = useState([]);
  const [connections, setConnections] = useState([]);
  const [selectedNode, setSelectedNode] = useState(null);
  const [configModalOpen, setConfigModalOpen] = useState(false);
  const [configTool, setConfigTool] = useState(null);
  const [dragging, setDragging] = useState(null);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [connectingFrom, setConnectingFrom] = useState(null);
  const [zoom, setZoom] = useState(1);
  const [pan, setPan] = useState({ x: 0, y: 0 });
  const [isPanning, setIsPanning] = useState(false);
  const [panStart, setPanStart] = useState({ x: 0, y: 0 });
  const [workflowName, setWorkflowName] = useState('');
  const [saveModalOpen, setSaveModalOpen] = useState(false);
  const canvasRef = useRef(null);
  const containerRef = useRef(null);
  const nodeIdCounter = useRef(0);

  const availableTools = [
    { name: 'Nmap', icon: 'nmap' },
    { name: 'Subfinder', icon: 'subfinder' },
    { name: 'Dirsearch', icon: 'dirsearch' },
    { name: 'Ghauri', icon: 'ghauri' }
  ];

  // Load workflow from localStorage on mount
  useEffect(() => {
    const saved = localStorage.getItem('cyberlynx-workflows');
    if (saved) {
      try {
        const workflows = JSON.parse(saved);
        if (workflows.length > 0) {
          // Load the most recent workflow
          const latest = workflows[workflows.length - 1];
          setNodes(latest.nodes || []);
          setConnections(latest.connections || []);
          setWorkflowName(latest.name || '');
          if (latest.nodeIdCounter) {
            nodeIdCounter.current = latest.nodeIdCounter;
          }
        }
      } catch (e) {
        console.error('Error loading workflow:', e);
      }
    }
  }, []);

  const addNode = (toolName) => {
    const newId = `node-${nodeIdCounter.current++}`;
    const newNode = {
      id: newId,
      name: toolName,
      position: {
        x: 200,
        y: 200
      },
      config: {}
    };
    setNodes([...nodes, newNode]);
  };

  const handleNodeClick = (node) => {
    setSelectedNode(node.id);
    setConfigTool(node);
    setConfigModalOpen(true);
  };

  const handleSaveConfig = (nodeId, config) => {
    setNodes(prevNodes =>
      prevNodes.map(node =>
        node.id === nodeId ? { ...node, config } : node
      )
    );
  };

  const handleDeleteNode = (nodeId) => {
    setNodes(prevNodes => prevNodes.filter(node => node.id !== nodeId));
    setConnections(prevConnections =>
      prevConnections.filter(conn => conn.from !== nodeId && conn.to !== nodeId)
    );
    if (selectedNode === nodeId) {
      setSelectedNode(null);
    }
  };

  const handleNodeMouseDown = (nodeId, e) => {
    e.stopPropagation();
    const node = nodes.find(n => n.id === nodeId);
    if (node) {
      setDragging(nodeId);
      const rect = containerRef.current?.getBoundingClientRect();
      if (rect) {
        setDragOffset({
          x: (e.clientX - rect.left - pan.x) / zoom - node.position.x,
          y: (e.clientY - rect.top - pan.y) / zoom - node.position.y
        });
      }
    }
  };

  const handleConnectionPointClick = (nodeId, pointType, e) => {
    e.stopPropagation();
    if (pointType === 'output') {
      if (connectingFrom === nodeId) {
        setConnectingFrom(null);
      } else {
        setConnectingFrom(nodeId);
      }
    } else if (pointType === 'input' && connectingFrom && connectingFrom !== nodeId) {
      const existingConnection = connections.find(
        conn => conn.from === connectingFrom && conn.to === nodeId
      );
      if (!existingConnection) {
        setConnections([...connections, { from: connectingFrom, to: nodeId }]);
      }
      setConnectingFrom(null);
    }
  };

  const handleMouseMove = (e) => {
    if (isPanning && containerRef.current) {
      const rect = containerRef.current.getBoundingClientRect();
      setPan({
        x: e.clientX - rect.left - panStart.x,
        y: e.clientY - rect.top - panStart.y
      });
    } else if (dragging && containerRef.current) {
      const rect = containerRef.current.getBoundingClientRect();
      setNodes(prevNodes =>
        prevNodes.map(node =>
          node.id === dragging
            ? {
                ...node,
                position: {
                  x: (e.clientX - rect.left - pan.x) / zoom - dragOffset.x,
                  y: (e.clientY - rect.top - pan.y) / zoom - dragOffset.y
                }
              }
            : node
        )
      );
    }
  };

  const handleMouseDown = (e) => {
    if (e.button === 1 || (e.button === 0 && e.ctrlKey)) {
      e.preventDefault();
      setIsPanning(true);
      const rect = containerRef.current?.getBoundingClientRect();
      if (rect) {
        setPanStart({
          x: e.clientX - rect.left - pan.x,
          y: e.clientY - rect.top - pan.y
        });
      }
    }
  };

  const handleMouseUp = () => {
    setDragging(null);
    setIsPanning(false);
  };

  const handleWheel = (e) => {
    e.preventDefault();
    const rect = containerRef.current?.getBoundingClientRect();
    if (rect) {
      const mouseX = e.clientX - rect.left;
      const mouseY = e.clientY - rect.top;

      const zoomFactor = e.deltaY > 0 ? 0.9 : 1.1;
      const newZoom = Math.max(0.3, Math.min(2, zoom * zoomFactor));

      const zoomChange = newZoom / zoom;
      setPan({
        x: mouseX - (mouseX - pan.x) * zoomChange,
        y: mouseY - (mouseY - pan.y) * zoomChange
      });
      setZoom(newZoom);
    }
  };

  useEffect(() => {
    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('mouseup', handleMouseUp);
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mouseup', handleMouseUp);
    };
  }, [dragging, dragOffset, isPanning, panStart, pan, zoom]);

  const handleZoomIn = () => setZoom(prev => Math.min(2, prev + 0.1));
  const handleZoomOut = () => setZoom(prev => Math.max(0.3, prev - 0.1));
  const handleResetZoom = () => {
    setZoom(1);
    setPan({ x: 0, y: 0 });
  };

  const handleSave = () => {
    if (!workflowName.trim()) {
      alert('Please enter a workflow name');
      return;
    }

    const workflow = {
      id: Date.now().toString(),
      name: workflowName,
      nodes,
      connections,
      nodeIdCounter: nodeIdCounter.current,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    };

    const saved = localStorage.getItem('cyberlynx-workflows');
    let workflows = saved ? JSON.parse(saved) : [];

    // Update existing or add new
    const existingIndex = workflows.findIndex(w => w.name === workflowName);
    if (existingIndex >= 0) {
      workflows[existingIndex] = { ...workflow, id: workflows[existingIndex].id };
    } else {
      workflows.push(workflow);
    }

    localStorage.setItem('cyberlynx-workflows', JSON.stringify(workflows));
    setSaveModalOpen(false);
    alert('Workflow saved successfully!');
  };

  const handleLoad = () => {
    const saved = localStorage.getItem('cyberlynx-workflows');
    if (saved) {
      try {
        const workflows = JSON.parse(saved);
        if (workflows.length > 0) {
          const workflowNames = workflows.map(w => w.name);
          const selected = prompt(`Enter workflow name to load:\n\nAvailable: ${workflowNames.join(', ')}`);
          if (selected) {
            const workflow = workflows.find(w => w.name === selected);
            if (workflow) {
              setNodes(workflow.nodes || []);
              setConnections(workflow.connections || []);
              setWorkflowName(workflow.name || '');
              if (workflow.nodeIdCounter) {
                nodeIdCounter.current = workflow.nodeIdCounter;
              }
              setZoom(1);
              setPan({ x: 0, y: 0 });
            } else {
              alert('Workflow not found');
            }
          }
        } else {
          alert('No saved workflows found');
        }
      } catch (e) {
        alert('Error loading workflow');
      }
    } else {
      alert('No saved workflows found');
    }
  };

  const drawConnection = (fromNode, toNode) => {
    const fromX = (fromNode.position.x + 60) * zoom + pan.x;
    const fromY = (fromNode.position.y + 70) * zoom + pan.y;
    const toX = (toNode.position.x + 60) * zoom + pan.x;
    const toY = toNode.position.y * zoom + pan.y;

    const midY = (fromY + toY) / 2;

    return (
      <path
        key={`${fromNode.id}-${toNode.id}`}
        d={`M ${fromX} ${fromY} C ${fromX} ${midY}, ${toX} ${midY}, ${toX} ${toY}`}
        stroke="rgba(255, 109, 31, 0.5)"
        strokeWidth={2 * zoom}
        fill="none"
        markerEnd="url(#arrowhead)"
        className="cursor-pointer hover:stroke-primary transition-colors"
      />
    );
  };

  return (
    <div className="relative w-full h-full">
      {/* Top Bar - Minimalist */}
      <div className="absolute top-0 left-0 right-0 z-30 bg-surface-light border-b border-gray-200 dark:border-gray-700 px-4 py-2 flex items-center justify-between">
        <div className="flex items-center space-x-4">
          <input
            type="text"
            value={workflowName}
            onChange={(e) => setWorkflowName(e.target.value)}
            placeholder="Workflow name..."
            className="px-3 py-1.5 text-sm bg-background-primary border border-gray-300 dark:border-gray-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary text-text-primary"
          />
          <button
            onClick={() => setSaveModalOpen(true)}
            className="px-3 py-1.5 text-sm bg-primary hover:bg-primary-dark text-white rounded-lg transition-colors flex items-center space-x-1.5"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 7H5a2 2 0 00-2 2v9a2 2 0 002 2h14a2 2 0 002-2V9a2 2 0 00-2-2h-3m-1 4l-3 3m0 0l-3-3m3 3V4" />
            </svg>
            <span>Save</span>
          </button>
          <button
            onClick={handleLoad}
            className="px-3 py-1.5 text-sm bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 text-text-primary rounded-lg transition-colors flex items-center space-x-1.5"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-8l-4-4m0 0L8 8m4-4v12" />
            </svg>
            <span>Load</span>
          </button>
        </div>

        {/* Zoom Controls */}
        <div className="flex items-center space-x-2">
          <button
            onClick={handleZoomOut}
            className="w-8 h-8 flex items-center justify-center bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 text-text-primary rounded-lg transition-colors"
            title="Zoom Out"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0zM13 10H7" />
            </svg>
          </button>
          <span className="text-xs text-text-secondary min-w-[3rem] text-center">{Math.round(zoom * 100)}%</span>
          <button
            onClick={handleZoomIn}
            className="w-8 h-8 flex items-center justify-center bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 text-text-primary rounded-lg transition-colors"
            title="Zoom In"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0zM10 7v6m3-3H7" />
            </svg>
          </button>
          <button
            onClick={handleResetZoom}
            className="px-2 py-1 text-xs bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 text-text-primary rounded-lg transition-colors"
            title="Reset View"
          >
            Reset
          </button>
        </div>
      </div>

      {/* Toolbar - Minimalist Sidebar */}
      <div className="absolute top-14 left-0 z-20 bg-surface-light border-r border-gray-200 dark:border-gray-700 p-3">
        <div className="space-y-2">
          {availableTools.map((tool) => (
            <button
              key={tool.name}
              onClick={() => addNode(tool.name)}
              className="w-10 h-10 flex items-center justify-center bg-primary hover:bg-primary-dark text-white rounded-lg transition-colors shadow-sm"
              title={tool.name}
            >
              <span className="text-xs font-semibold">{tool.name[0]}</span>
            </button>
          ))}
        </div>
        {nodes.length > 0 && (
          <>
            <div className="my-3 border-t border-gray-200 dark:border-gray-700"></div>
            <button
              onClick={() => {
                setNodes([]);
                setConnections([]);
                setSelectedNode(null);
                setConnectingFrom(null);
                setWorkflowName('');
              }}
              className="w-10 h-10 flex items-center justify-center bg-danger hover:bg-red-600 text-white rounded-lg transition-colors"
              title="Clear All"
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16"></path>
              </svg>
            </button>
          </>
        )}
      </div>

      {/* Canvas Container */}
      <div
        ref={containerRef}
        className="absolute top-14 left-12 right-0 bottom-0 bg-background-primary overflow-hidden"
        style={{ cursor: isPanning ? 'grabbing' : 'default' }}
        onMouseDown={handleMouseDown}
        onWheel={handleWheel}
        onClick={() => {
          setSelectedNode(null);
          setConnectingFrom(null);
        }}
      >
        {/* SVG for connections */}
        <svg className="absolute inset-0 pointer-events-none" style={{ width: '100%', height: '100%' }}>
          <defs>
            <marker
              id="arrowhead"
              markerWidth="10"
              markerHeight="10"
              refX="9"
              refY="3"
              orient="auto"
            >
              <polygon
                points="0 0, 10 3, 0 6"
                fill="rgba(255, 109, 31, 0.5)"
              />
            </marker>
          </defs>
          {connections.map((conn, index) => {
            const fromNode = nodes.find(n => n.id === conn.from);
            const toNode = nodes.find(n => n.id === conn.to);
            if (fromNode && toNode) {
              return (
                <g key={`${conn.from}-${conn.to}-${index}`}>
                  {drawConnection(fromNode, toNode)}
                  <path
                    d={`M ${(fromNode.position.x + 60) * zoom + pan.x} ${(fromNode.position.y + 70) * zoom + pan.y} C ${(fromNode.position.x + 60) * zoom + pan.x} ${((fromNode.position.y + 70) * zoom + pan.y + toNode.position.y * zoom + pan.y) / 2}, ${(toNode.position.x + 60) * zoom + pan.x} ${((fromNode.position.y + 70) * zoom + pan.y + toNode.position.y * zoom + pan.y) / 2}, ${(toNode.position.x + 60) * zoom + pan.x} ${toNode.position.y * zoom + pan.y}`}
                    stroke="transparent"
                    strokeWidth={20 * zoom}
                    fill="none"
                    className="cursor-pointer pointer-events-auto"
                    onClick={(e) => {
                      e.stopPropagation();
                      if (e.detail === 2) {
                        setConnections(prev => prev.filter((c, i) => i !== index));
                      }
                    }}
                  />
                </g>
              );
            }
            return null;
          })}
        </svg>

        {/* Transform Container for Zoom/Pan */}
        <div
          ref={canvasRef}
          className="absolute"
          style={{
            transform: `translate(${pan.x}px, ${pan.y}px) scale(${zoom})`,
            transformOrigin: '0 0'
          }}
        >
          {/* Nodes */}
          {nodes.map((node) => (
            <div key={node.id} className="relative">
              <ToolNode
                tool={node}
                position={node.position}
                onClick={() => handleNodeClick(node)}
                isSelected={selectedNode === node.id}
                onConnectionPointClick={handleConnectionPointClick}
                isConnectingFrom={connectingFrom === node.id}
              />
              {selectedNode === node.id && (
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDeleteNode(node.id);
                  }}
                  className="absolute -top-2 -right-2 w-5 h-5 bg-danger hover:bg-red-600 text-white rounded-full flex items-center justify-center shadow-lg z-20 transition-all duration-200"
                  style={{
                    left: `${node.position.x + 140}px`,
                    top: `${node.position.y}px`
                  }}
                >
                  <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
                  </svg>
                </button>
              )}
              <div
                className="absolute cursor-move"
                style={{
                  left: `${node.position.x}px`,
                  top: `${node.position.y}px`,
                  width: '120px',
                  height: '60px'
                }}
                onMouseDown={(e) => {
                  if (e.target === e.currentTarget || !e.target.closest('[class*="connection"]')) {
                    handleNodeMouseDown(node.id, e);
                  }
                }}
              ></div>
            </div>
          ))}
        </div>

        {/* Empty state */}
        {nodes.length === 0 && (
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="text-center">
              <svg className="w-16 h-16 mx-auto text-text-secondary opacity-30 mb-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z"></path>
              </svg>
              <p className="text-text-secondary text-sm font-medium">Empty canvas</p>
              <p className="text-text-secondary text-xs mt-1 opacity-70">Add tools from the sidebar</p>
            </div>
          </div>
        )}

        {/* Minimalist Help */}
        {nodes.length > 0 && (
          <div className="absolute bottom-3 right-3 z-10 bg-surface-light bg-opacity-90 backdrop-blur-sm rounded-lg px-3 py-2 text-xs text-text-secondary">
            <div className="flex items-center space-x-4">
              <span>Ctrl + Drag to pan</span>
              <span></span>
              <span>Scroll to zoom</span>
            </div>
          </div>
        )}
      </div>

      {/* Configuration Modal */}
      {configTool && (
        <ToolConfigModal
          tool={configTool}
          isOpen={configModalOpen}
          onClose={() => {
            setConfigModalOpen(false);
            setConfigTool(null);
          }}
          onSave={handleSaveConfig}
        />
      )}

      {/* Save Modal */}
      {saveModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50" onClick={() => setSaveModalOpen(false)}>
          <div className="bg-surface-light rounded-xl p-6 max-w-md w-full mx-4" onClick={(e) => e.stopPropagation()}>
            <h3 className="text-lg font-semibold text-text-primary mb-4">Save Workflow</h3>
            <input
              type="text"
              value={workflowName}
              onChange={(e) => setWorkflowName(e.target.value)}
              placeholder="Enter workflow name..."
              className="w-full px-3 py-2 bg-background-primary border border-gray-300 dark:border-gray-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary text-text-primary mb-4"
              autoFocus
            />
            <div className="flex justify-end space-x-2">
              <button
                onClick={() => setSaveModalOpen(false)}
                className="px-4 py-2 text-sm bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600 text-text-primary rounded-lg transition-colors"
              >
                Cancel
              </button>
              <button
                onClick={handleSave}
                className="px-4 py-2 text-sm bg-primary hover:bg-primary-dark text-white rounded-lg transition-colors"
              >
                Save
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default WorkflowCanvas;
