import React, { useState, useRef, useEffect } from 'react';
import WorkflowCanvas from '../components/workflow/WorkflowCanvas';
import WorkflowTerminal from '../components/workflow/WorkflowTerminal';

const Workflow = () => {
  const [nodes, setNodes] = useState([]);
  const [connections, setConnections] = useState([]);
  const [isTerminalMinimized, setIsTerminalMinimized] = useState(false);
  const [terminalHeight, setTerminalHeight] = useState(300);
  const [sidebarWidth, setSidebarWidth] = useState(240);
  const containerRef = useRef(null);

  useEffect(() => {
    // Detect sidebar width from DOM
    const sidebar = document.getElementById('sidebar');
    if (sidebar) {
      const updateSidebarWidth = () => {
        const width = sidebar.offsetWidth;
        setSidebarWidth(width);
      };
      updateSidebarWidth();

      const observer = new MutationObserver(updateSidebarWidth);
      observer.observe(sidebar, { 
        attributes: true, 
        attributeFilter: ['class'],
        childList: false,
        subtree: false
      });

      window.addEventListener('resize', updateSidebarWidth);

      return () => {
        observer.disconnect();
        window.removeEventListener('resize', updateSidebarWidth);
      };
    }
  }, []);


  useEffect(() => {
    const handleMouseMove = (e) => {
      if (isResizing) {
        const newHeight = window.innerHeight - e.clientY;
        setTerminalHeight(Math.max(200, Math.min(600, newHeight)));
      }
    };

    const handleMouseUp = () => {
      setIsResizing(false);
    };

    if (isResizing) {
      window.addEventListener('mousemove', handleMouseMove);
      window.addEventListener('mouseup', handleMouseUp);
      return () => {
        window.removeEventListener('mousemove', handleMouseMove);
        window.removeEventListener('mouseup', handleMouseUp);
      };
    }
  }, [isResizing]);

  return (
    <div className="h-full flex flex-col">
      <div className="mb-4">
        <h2 className="text-2xl font-semibold text-text-primary mb-1">Workflow Builder</h2>
        <p className="text-sm text-text-secondary">Design and customize your security assessment workflows</p>
      </div>
      <div ref={containerRef} className="flex-1 flex flex-col gap-0 overflow-hidden relative">
        <div 
          className="bg-surface-light rounded-lg overflow-hidden transition-all duration-200"
          style={{ 
            marginBottom: isTerminalMinimized ? '0' : '0',
            paddingBottom: isTerminalMinimized ? '0' : `${terminalHeight}px`
          }}
        >
          <WorkflowCanvas 
            onNodesChange={setNodes}
            onConnectionsChange={setConnections}
          />
        </div>
      </div>
      {/* Fixed Terminal at Bottom */}
      {!isTerminalMinimized && (
        <div 
          className="fixed bottom-0 bg-transparent flex-shrink-0 z-50 shadow-2xl"
          style={{ 
            height: `${terminalHeight}px`, 
            maxHeight: '60%',
            left: `${sidebarWidth}px`,
            right: '0'
          }}
        >
          <WorkflowTerminal 
            nodes={nodes} 
            connections={connections}
            isMinimized={isTerminalMinimized}
            onMinimize={() => setIsTerminalMinimized(true)}
            onMaximize={() => setIsTerminalMinimized(false)}
            height={terminalHeight}
          />
        </div>
      )}
      {/* Minimized Terminal Button */}
      {isTerminalMinimized && (
        <button
          onClick={() => setIsTerminalMinimized(false)}
          className="fixed bottom-4 right-20 w-14 h-14 bg-gradient-to-r from-gray-800 to-gray-900 hover:from-gray-700 hover:to-gray-800 text-white rounded-full shadow-lg hover:shadow-xl transform hover:scale-110 transition-all duration-200 flex items-center justify-center z-40"
          title="Open Terminal"
          style={{ zIndex: 9998, left: `${sidebarWidth + 20}px` }}
        >
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 10h.01M12 10h.01M16 10h.01M9 16H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-5l-5 5v-5z" />
          </svg>
        </button>
      )}
    </div>
  );
};

export default Workflow;
