import React, { useState } from 'react';

const AddTargetModal = ({ isOpen, onClose, onAddTarget }) => {
  console.log('AddTargetModal isOpen:', isOpen);
  const [type, setType] = useState('domain');
  const [value, setValue] = useState('');
  const [description, setDescription] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await onAddTarget({ type, value, description });
      // Clear form fields
      setType('domain');
      setValue('');
      setDescription('');
      onClose();
    } catch (error) {
      console.error('Error adding target:', error);
      // Don't close modal on error so user can retry
    }
  };

  if (!isOpen) return null;

  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  return (
    <div 
      className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50"
      onClick={handleBackdropClick}
    >
      <div 
        className="bg-surface-light p-6 rounded-lg shadow-lg w-11/12 md:w-1/2 lg:w-1/3"
        onClick={(e) => e.stopPropagation()}
      >
        <h2 className="text-2xl font-bold text-text-primary mb-4">Add New Target</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label htmlFor="targetType" className="block text-text-secondary text-sm font-bold mb-2">
              Target Type
            </label>
            <select
              id="targetType"
              className="shadow border rounded w-full py-2 px-3 text-text-primary leading-tight focus:outline-none focus:shadow-outline bg-surface-dark border-border-color"
              value={type}
              onChange={(e) => setType(e.target.value)}
              required
            >
              <option value="domain">Domain</option>
              <option value="ip">IP Address</option>
              <option value="url">URL</option>
            </select>
          </div>
          <div className="mb-4">
            <label htmlFor="targetValue" className="block text-text-secondary text-sm font-bold mb-2">
              Value
            </label>
            <input
              type="text"
              id="targetValue"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-text-primary leading-tight focus:outline-none focus:shadow-outline bg-surface-dark border-border-color"
              value={value}
              onChange={(e) => setValue(e.target.value)}
              placeholder="e.g., example.com or 192.168.1.1"
              required
            />
          </div>
          <div className="mb-6">
            <label htmlFor="targetDescription" className="block text-text-secondary text-sm font-bold mb-2">
              Description
            </label>
            <textarea
              id="targetDescription"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-text-primary leading-tight focus:outline-none focus:shadow-outline bg-surface-dark border-border-color h-24"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="e.g., Main company website for penetration testing"
            ></textarea>
          </div>
          <div className="flex items-center justify-between">
            <button
              type="submit"
              className="btn btn-primary"
            >
              Add Target
            </button>
            <button
              type="button"
              onClick={onClose}
              className="btn btn-secondary"
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddTargetModal;
